package lt.lhu.unit02.main;

public class Task02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		int b;
		int s;
		
		double c;
		double sq;
		
		a = 4;
		b = 7;
		s = 2;
		
		c = Math.sqrt( Math.pow(a, s) + Math.pow(b, s));
		sq = (a * b) / 2;
		
		System.out.println("c=" + c);
		System.out.println("sq=" + sq);
		
		

	}

}
